import React, { Component } from 'react';
import SingleNews from './common/singleNews';
import ListNews from './common/listNews';
import SectionHeader from './common/sectionHeader';


class FiftyFiftyRightList extends Component {
    state = {  }
    render() { 
        const { news } = this.props;
       
        if (!news) return false;
        
        const firstnews = news[0];
        const otherNews = news.slice(1);

        return ( 
            <React.Fragment>
                <div className="widget-f-flist" >
                    <SectionHeader headerText="অর্থনীতি" headerLink={firstnews} />
                    <SingleNews key={ firstnews.id } content={ firstnews } />
                    <ListNews content={ otherNews } />
                    
                </div>
            </React.Fragment>
         );
    }
}
 
export default FiftyFiftyRightList;
