import React, { Component } from 'react';
import SectionHeader from './common/sectionHeader';
import SingleNews from './common/singleNews';
import ListNewsWithoutImage from './common/listNewWithoutList';
import config from "../../config";


class ThreeColumnlocalNav extends Component {
    state = {}
    render() {
        {
            const { news } = this.props;
           
            if (!news) return false;
            
            const firstnews = news[0];
            const otherNews = news.slice(1);
    
            return (
                <React.Fragment>
                    <div className="threeColumnlocalNav-container">
                        <div className="thColNav-row-one" >
                            <SectionHeader
                                headerText={firstnews['sections'][0]['name']}
                                headerLink={firstnews['sections'][0]['section-url']} />
                            <SingleNews key={firstnews.id}
                                content={firstnews}
                                parentClassName=""
                                imageWidth="290"
                                imageWidthT="800"
                                imageWidthM="450"
                            />
                            <ListNewsWithoutImage content={otherNews}
                                excerpt="yes"
                                parentClassName="" />
                           
                        </div>
                        <div className="thColNav-row-two" >
                            
                            <SectionHeader
                                headerText={firstnews['sections'][0]['name']}
                                headerLink={firstnews['sections'][0]['section-url']} />
                            <SingleNews key={firstnews.id}
                                content={firstnews}
                                parentClassName=""
                                imageWidth="290"
                                imageWidthT="800"
                                imageWidthM="450" />
                            <ListNewsWithoutImage content={otherNews}
                                excerpt="yes"
                                parentClassName="FiftyFiftyRightListParentClass" />
                            <input type="button" value={config.moreBn} className="more-news" />
                        </div>
                    </div>
                </React.Fragment>
            );
        
        }
    }
}
 
export default ThreeColumnlocalNav;
