import React, { Component } from 'react';
// import Loader from './common/loading';
import Footer from './common/footer';
import http from '../services/httpService';
import { ToastContainer, toast } from 'react-toastify';
import config from '../config';
// import _ from 'lodash';
import 'react-toastify/dist/ReactToastify.css';
import FiftyFiftyRightList from './widgets/fiftyFiftyRightList';

toast.configure();

class HomePage extends Component {
    state = {
        news: [],
    };

    async  componentDidMount() {

        const { data:news } =
            await http.get( config.endPoint + config.fiftyFiftyRightListLimit);
       
        console.log(news);
        this.setState({ news });
    }

    render() { 
        // if (this.state.loading)
        //     return <Loader message={this.state.loadingMessage} />;

        const { news } = this.state;
        console.log(news.stories)
        return (
            <React.Fragment>
               
                <ToastContainer /> 
               <FiftyFiftyRightList news ={ news.stories } /> 
                <Footer/>

            </React.Fragment>
        )
    }; 
}
 
export default HomePage;
