import React, { Component } from 'react';
import config from '../../../config'
import { NavLink } from 'react-router-dom';

class SingleNews extends Component {

    render() { 
        const { content, parentClassName } = this.props;
        parentClassName = parentClassName + ' left-fifp'   
        return ( 

            <div className="left-fifp" >
                <NavLink to={content['slug']} >
                <img width="100%" src={config.imageHost + content['hero-image-s3-key']}
                    alt={content['headline']} />
                <p className="title">{content['headline']}</p> 
                <p>{content['headline']
                    + ' ' + content['headline']
                    + ' ' + content['headline']
                    + ' ' + content['headline']}
                    </p>
                </NavLink>
            </div>

         );
    }
}
 
export default SingleNews;
