import React, { Component } from 'react';
import config from '../../../config'
import { NavLink } from 'react-router-dom';

class SingleNews extends Component {

    render() { 
        const { content, parentClassName } = this.props;
        const divClassName = parentClassName + ' left-fifp';   
        return ( 

            <div className={ divClassName }>
                <NavLink to={content['slug']} >
                    <picture>
                       
                        <img src={config.imageHost + content['hero-image-s3-key'+"?rect=0%2C554%2C2695%2C1516&w=622&auto=format%2Ccompress"]} alt={content['headline']}  />
                    </picture>
                {/* <img width="100%" src={config.imageHost + content['hero-image-s3-key']}
                    alt={content['headline']} /> */}
                <p className="title">{content['headline']}</p> 
                <p>{content['headline']
                    + ' ' + content['headline']
                    + ' ' + content['headline']
                    + ' ' + content['headline']}
                    </p>
                </NavLink>
            </div>

         );
    }
}
 
export default SingleNews;
