import React, { Component } from 'react';
import SingleNews from './common/singleNews';
import config from "../../config";


class BlackVideo extends Component {

    render() { 
        const { news } = this.props;
           
        if (!news) return false;
        
        const firstnews = news[0]; 
        const news2t3 = news.slice(1, 3);
        const news4t5 = news.slice(3, 6);
        
        return ( 
            <div className="black-videp-widget-whole">
                <div className="black-videp-widget">
                    <div className="itemText">
                        {firstnews['headline']}
                    </div>
                    <div className="itemImage">
                        <img width="100%"
                            src={config.imageHost + firstnews['hero-image-s3-key']}
                            alt={firstnews['headline']}/>  
                    </div>
                </div>
                <div className="blackList">
                {news2t3.map(eachnews =>
                                        <SingleNews key={eachnews.id}
                                            content={eachnews}
                                            parentClassName=""
                                            excerpt="no"
                                            imageWidth="290"
                                            imageWidthT="800"
                                            imageWidthM="450"/>
                                    )}
                </div>
            </div>


// <SingleNews
// content={firstnews}
// parentClassName=""
// imageWidth="580"
// imageWidthT="800"
// imageWidthM="450"/>
         );
    }
}
 
export default BlackVideo;
